INSERT INTO `beeldr_rijnab_vveigenaren` (`id`, `body`, `title`)
VALUES
	(1,'<p>Een <strong>Vereniging van Eigenaren</strong><span>&nbsp;</span>(VvE) is in Nederland een vereniging van de eigenaren van de appartementsrechten van de woningen die tot een appartementencomplex behoren. De vereniging van eigenaars zorgt voor het regelen van het onderhoud van het appartementsgebouw en alle andere gemeenschappelijke belangen van de eigenaren.</p>\r\n\r\n<p>Als een woongebouw door middel van een splitsingsakte wordt gesplitst in appartementen die los verkocht kunnen worden, wordt er automatisch een vereniging van eigenaars opgericht. De eigenaren van de appartementen zijn automatisch (\'van rechtswege\') lid van die vereniging.</p>\r\n\r\n<p>De regels die aan de wijze van functioneren van een Vereniging van Eigenaars worden gesteld (statuten, bestuur, ledenvergadering en dergelijke), zijn gelijk aan die van een vereniging in het algemeen, met enkele bijzondere aanvullingen.</p>','Vveigenaren Pagina');

